import React from 'react';

const Featured = (props) => {
  const teacher= (`${props.match.params.fname} ${props.match.params.lname}`).toUpperCase();
  const topic =props.match.params.topic;
  function capital(data){
    return data.charAt(0).toUpperCase() + data.substring(1);
  }
  console.log(capital("raman"));
  return (
    <div className="main-content">
      <h2>{capital(teacher)} </h2>
      <p>Introducing <strong>{capital(teacher)}</strong>, a teacher who loves teaching courses about <strong>{topic}</strong>!</p>
    </div>
  );
}

export default Featured;
